import React, { Component } from 'react';
import './App.css';
// Add the Route named import
import { Route, Switch, Redirect } from 'react-router-dom';
import AuthPage from './pages/AuthPage/AuthPage'
import IndexPage from './pages/IndexPage/IndexPage';
import { Link } from 'react-router-dom';

class App extends Component {

  state = {
    user: null,
  }

  // a method to update user
  setUserInState = (incomingUserData) => {
    this.setState({ user: incomingUserData})
  }

  componentDidMount() {
    let token = localStorage.getItem('token')
    if (token) {
      const payload = JSON.parse(atob(token.split('.')[1])); // decode token
      if (payload.exp < Date.now() / 1000) {  // Check if our token is expired, and remove if it is (standard/boilerplate)
        localStorage.removeItem('token');
        token = null;
      } else { // token not expired! our user is still 'logged in'. Put them into state.
        let userDoc = payload.user // grab user details from token
        this.setState({user: userDoc})      
      }
    }
  }

  render() {
    return (
      <main className="App">
          <nav>
            <Link to='/'>Resumates</Link>
          </nav>

        {/* this ternary operator asks: is there a user in state? */}
        {/* if yes, they can see our pages: neworder, etc. */}
        {/* if no(user is null), show them only the <AuthPage> */}
        { this.state.user ? 
          <Switch>
            <Route path='/index' render={(props) => (
              <IndexPage {...props}/>
            )}/>
            <Redirect to="/index" />
          </Switch>
          :
          <AuthPage setUserInState={this.setUserInState}/>
        }

      <section className="Intro"> 
        <h1>How it Works</h1>
        <div>
          <h2>Upload</h2>
          <p>...your resume annonymously to gain access 
            to our database</p>
        </div>
        <div>
          <h2>Search</h2>
          <p>...our database for resumes based on specific critera</p>
        </div>
        <div>
          <h2>Download</h2>
          <p>... resumes you like for future use</p>
        </div>
        <div>
          <h2>Share & Receive Feedback</h2>
          <p>...with other industry industry professionals</p>
        </div>
      </section>
      </main>
    );

  }
}

export default App;
